mapper.select:
SELECT k.no, k.title, k.contents, k.category, k.price, k.pictures, k.views, k.post_date, COALESCE(average_table.avg_rating, 0) AS average_rating, m.nickname FROM mealkit k LEFT JOIN ( SELECT mv.mealkit_no, AVG(rating) as avg_rating FROM mealkit_review mv GROUP BY mv.mealkit_no ) average_table ON k.no=average_table.mealkit_no JOIN member m ON k.id = m.id
WHERE k.category = #{category}
ORDER BY k.post_date DESC

SELECT k.*, COALESCE(average_table.avg_rating, 0) AS average_rating, m.nickname FROM mealkit k LEFT JOIN ( SELECT mv.mealkit_no, AVG(rating) as avg_rating FROM mealkit_review mv GROUP BY mv.mealkit_no ) average_table ON k.no=average_table.mealkit_no JOIN member m ON k.id = m.id
WHERE k.id = #{id}
ORDER BY k.post_date DESC

mapper.update:
UPDATE mealkit 
SET title = #{title}, contents = #{contents}, category = #{category}, price = #{price}, stock = #{stock}, 
	pictures = #{pictures}, orders = #{orders}, origin = #{origin}
WHERE no = #{no}

mapper.select:
SELECT price
FROM mealkit
WHERE no = #{mealkitNo}
