mapper.select:

    	SELECT COUNT(*)
		FROM mealkit_order
		WHERE id=#{id} AND delivered=#{delivered}

mapper.update:
		UPDATE 
    		mealkit_order 
		SET delivered = #{delivered}, refund = #{refund} 
		WHERE no = #{orderNo}

